const Contact = () => {
  return (
    <section 
    id="contact"
    className=""
    >
        <div className="container lg:grid lg:grid-cols-2 lg:items-stretch lg:mt-28 mt-20 ">
            
            <div className="mb-12 lg:mb-0 lg:flex lg:flex-col">
                <h2 className="headline-3 lg:max-w-[12ch] reveal-up">
                    Contact me for collaboration
                </h2>

                <p className="text-zinc-400 mt-3 mb-8 max-w-[50ch] lg:max-w-[30ch] reveal-up">
                    Reach out today to discuss your project needs and start collaborating on something amazing!
                </p>

                <div className="flex items-center gap-2 mt-auto">
                    {socialLinks.map(({href, icon, alt}, key) => (
                        <a 
                          key={key} 
                          href={href} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="w-12 h-12 grid place-items-center ring-inset ring-2 ring-zinc-50/5 transitions[background-color,color] hover:bg-zinc-50 rounded-lg hover:text-zinc-950 active:bg-zinc-50/80 reveal-up"
                          >
                          {icon}
                         
                        </a>
                    ))}
                </div>
            </div>

            <form 
            action="https://getform.io/f/bvrrqngb"
            method="POST"
            className="xl:pl-10 2xl:pl-20"
            >

            <div className="md:grid md:items-center md:grid-col-2 md:gap-2">

                    <div className="mb-4 ">
                        <label 
                            htmlFor="name"
                            className="label reveal-up">
                            Name
                        </label>
                        <input 
                            type="text"
                            id="name"
                            name="name"
                            autoComplete="name"
                            placeholder="Wisdom Michael"
                            required
                            className="text-field"
                        />
                    </div>

                    <div className="mb-4">
                        <label 
                            htmlFor="email"
                            className="label reveal-up">
                            Email
                        </label>
                        <input 
                            type="email"
                            id="email"
                            name="email"
                            autoComplete="email"
                            placeholder="wisdom@example.com"
                            required
                            className="text-field reveal-up"
                        />
                    </div>
            </div>

            <div className="mb-4">
                <label 
                htmlFor="message"
                className="label reveal-up">
                    Message
                </label>
                <textarea 
                    id="message"
                    name="message"
                    autoComplete="message"
                    placeholder="Write your message here..."
                    required
                    className="text-field resize-y min-h-32 max-h-80 reveal-up"
                />
            </div>

            <button 
            type="submit" 
            className="btn btn-primary [&]:max-w-full w-full justify-center reveal-up"
            >
                Submit
            </button>
            </form>
        </div>
    </section>
  )
}

export default Contact